package com.hicapital.comm.outefms;

import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.net.Socket;
import java.net.UnknownHostException;

public class OutEFMS {

  /**
   * @param args
   */
  public static void main(String[] args) {
    // TODO Auto-generated method stub
    Socket xS = null;
    BufferedReader in;
    BufferedWriter out;

    try {
      xS = new Socket("localhost", 9020);
      in  = new BufferedReader(new InputStreamReader(xS.getInputStream()));
      out = new BufferedWriter(new OutputStreamWriter(xS.getOutputStream()));
      
      String command = "";
      //                
      //                
      //                
      out.write(command);
      out.flush();

      xS.close();
      
    } catch (UnknownHostException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
    } catch (IOException e) {
      // TODO Auto-generated catch block
      e.printStackTrace();
    }

    
  }

}
